sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Check if 'Additional comments' field has changed
    if (current.comments.changes()) {
        // Query for all active HR Tasks associated with this HR Ticket
        var hrTask = new GlideRecord('x_1298059_hr_hr_task'); // Replace 'hr_task' with the actual table name of your HR Tasks
        hrTask.addQuery('parent', current.sys_id); // Assuming 'parent' is the field that relates tasks to the ticket
        hrTask.addQuery('state', 'open'); // Only select active tasks
        hrTask.query();

        // Iterate through the list of tasks and update the work notes
        while (hrTask.next()) {
            hrTask.work_notes = current.comments.getJournalEntry(1); // Get the most recent entry
            hrTask.update();
        }
    }
})(current, previous);
